import '../../App.css'
import React from 'react';
import styled from 'styled-components'
import imgRestaurants from "../../images/restaurants.jpg"

const Categorie = styled.div`

background-image: url(${imgRestaurants});
background-size: cover;

& h3 {

color: white;
font-weight: bold;
text-align: center;

}
  `

export default () => {
    return (
        <div>
            <Categorie>
               <h3 className="p-4">RESTAURANTS</h3>
            </Categorie>
            <Categorie>
                <h3 className="p-4">ACCOMODATION</h3>
            </Categorie>
            <Categorie>
                <h3 className="p-4">TRANSPORTS</h3>
            </Categorie>

        </div>
    )

}
